INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (105015, 'Trazodone', 'Methysergide', 'Moderate', 'Serotonin antagonists such as cyproheptadine and methysergide may diminish the pharmacologic effects of selective serotonin reuptake inhibitors (SSRIs) and other highly serotonergic agents (e.g., 5-hydroxytryptophan; buspirone; mirtazapine; nefazodone; sibutramine; St. John''s wort; trazodone; SNRIs such as duloxetine, desvenlafaxine, milnacipran, and venlafaxine). This pharmacodynamic interaction is sometimes exploited in the use of cyproheptadine and methysergide for the treatment of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Theoretically, serotonergic agents may also antagonize the pharmacologic effects of cyproheptadine and methysergide, although this effect has not been reported.', 'DDInter', 'Patients should be monitored for potentially diminished therapeutic response to serotonergic agents during coadministration with cyproheptadine or methysergide, and dosages adjusted as necessary.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105134/', '', 'Rimegepant, Eptinezumab, Ubrogepant, Galcanezumab, Atogepant, Erenumab, Fremanezumab', 'Phenelzine, Desipramine, Tranylcypromine, Trimipramine, Maprotiline, Isocarboxazid, Amitriptyline, Nortriptyline, Amoxapine, Clomipramine, Imipramine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (105016, 'Trazodone', 'Metoclopramide', 'Moderate', 'Coadministration of metoclopramide with serotonin reuptake inhibitors has been associated with development of the serotonin syndrome and severe extrapyramidal reactions. The exact mechanism is unknown but may involve a pharmacodynamic interaction between serotonergic and antidopaminergic effects of the drugs. A pharmacokinetic interaction is also possible, since metoclopramide and most serotonin reuptake inhibitors are primarily or at least partially metabolized by the CYP450 2D6 isoenzyme.', 'DDInter', 'Caution is advised if metoclopramide is prescribed in combination with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitors (SNRIs). Patients should be monitored for symptoms of the serotonin syndrome as well as development of extrapyramidal reactions such as involuntary twitching of the jaw and limbs, teeth clenching, severe jerking, trismus, and tongue and neck stiffness. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary. Moderately ill patients may also benefit from the administration of a serotonin antagonist (e.g., cyproheptadine, chlorpromazine). Severe cases should be managed under consultation with a toxicologist and may require sedation, neuromuscular paralysis, intubation, and mechanical ventilation in addition to the other measures.', 'Others', 'Caution is advised if metoclopramide is prescribed in combination with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitors (SNRIs).', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105135/', '', 'Oxitriptan, St. John''s Wort, Tryptophan', '', 1767369485);
