INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (105301, 'Amprenavir', 'Ritonavir', 'Moderate', 'The concomitant administration of ritonavir may significantly increase amprenavir AUC and trough concentrations. The mechanism is inhibition of CYP450 3A4 metabolism by ritonavir.', 'DDInter', 'The manufacturer recommends that the adult amprenavir dosage be reduced to 1200 mg once daily with ritonavir 200 mg once daily or 600 mg twice daily with ritonavir 100 mg twice daily.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105420/', '', 'Glecaprevir, Cabotegravir, Amprenavir, Zanamivir, Valaciclovir, Lenacapavir, Velpatasvir, Rilpivirine, Oseltamivir, Brincidofovir, Remdesivir, More', 'Paritaprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Oseltamivir, Darunavir, Maribavir, Rimantadine, Elvitegravir, Telbivudine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (105302, 'Paritaprevir', 'Atazanavir', 'Major', 'Coadministration of atazanavir plus ritonavir in combination with ombitasvir/paritaprevir/ritonavir plus dasabuvir may significantly increase the plasma concentrations of paritaprevir. The mechanism may involve inhibition of both CYP450 3A4-mediated metabolism and P-glycoprotein-mediated transport by atazanavir and ritonavir. Coadministration of atazanavir with ombitasvir/paritaprevir/ritonavir plus dasabuvir may increase the risk of hyperbilirubinemia. Both atazanavir and paritaprevir can cause elevations in indirect (unconjugated) bilirubin, the former by inhibition of UGT1A1 and the latter by inhibition of OATP1B1/1B3.', 'DDInter', 'When coadministered with ombitasvir/paritaprevir/ritonavir plus dasabuvir, atazanavir 300 mg should be given in the morning without ritonavir. The ritonavir component of the HIV antiretroviral regimen should be restarted after completion of treatment with ombitasvir/paritaprevir/ritonavir plus dasabuvir.', 'Metabolism', 'When coadministered with ombitasvir/paritaprevir/ritonavir plus dasabuvir, atazanavir 300 mg should be given in the morning without ritonavir.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105421/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, More', 'Cabotegravir, Amprenavir, Zanamivir, Velpatasvir, Rilpivirine, Oseltamivir, Cobicistat, Remdesivir, Famciclovir, Darunavir, Maribavir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (105303, 'Bedaquiline', 'Ritonavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of bedaquiline, which is primarily metabolized by CYP450 3A4 to its less active N-monodesmethyl metabolite, M2.', 'DDInter', 'The concomitant use of potent CYP450 3A4 inhibitors such as clarithromycin, cobicistat, conivaptan, delavirdine, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents given systemically for more than 14 consecutive days should generally be avoided during treatment with bedaquiline unless the benefit is anticipated to outweigh the risk. Appropriate clinical monitoring for bedaquiline-related adverse reactions is recommended if coadministration is required. ECG, serum electrolytes (potassium, magnesium, calcium), and liver function tests (ALT, AST, alkaline phosphatase, bilirubin) should be monitored before starting bedaquiline therapy and periodically during treatment in accordance with the product labeling. Hypokalemia, hypomagnesemia, and hypocalcemia must be corrected prior to bedaquiline administration. Bedaquiline should be interrupted in patients who develop clinically significant ventricular arrhythmia or a QTcF interval greater than 500 msec confirmed by repeat ECG. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'The concomitant use of potent CYP450 3A4 inhibitors such as clarithromycin, cobicistat, conivaptan, delavirdine, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents given systemically for more than 14 consecutive days should generally be avoided during treatment with bedaquiline unless the benefit is anticipated to outweigh the risk.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105422/', '', 'Paritaprevir, Glecaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Capreomycin, Rifamycin, Streptomycin, Cycloserine, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (105304, 'Bempedoic acid', 'Paritaprevir', 'Moderate', 'Coadministration with bempedoic acid may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of the uptake transporters organic anion transporting polypeptide (OATP) 1B1 and 1B3. The proposed mechanism, based on in vitro data, is decreased clearance due to bempedoic acid-mediated inhibition of OATP1B1 and 1B3.', 'DDInter', 'Caution is advised if bempedoic acid is used concomitantly with drugs that are substrates of OATP1B1 or 1B3, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever bempedoic acid is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if bempedoic acid is used concomitantly with drugs that are substrates of OATP1B1 or 1B3, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105423/', '', 'Lamivudine, Tenofovir alafenamide, Maraviroc, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, More', 'Cholestyramine, Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Probucol, Cerivastatin, Lomitapide, Ezetimibe, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (105305, 'Berotralstat', 'Ritonavir', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations of berotralstat. Berotralstat is a substrate of both P-gp and BCRP.', 'DDInter', 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors. Patients should be advised to contact their physician if they experience undue adverse effects of berotralstat such as abdominal pain, vomiting, or diarrhea. Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Absorption', 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105424/', '', 'Lamivudine, Cabotegravir, Simeprevir, Zanamivir, Valaciclovir, Telaprevir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, More', 'Hemin, Conestat alfa, Hyaluronidase, Streptokinase, Human C1-esterase inhibitor, Crizanlizumab, Lanadelumab, Icatibant', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (105306, 'Boceprevir', 'Ritonavir', 'Moderate', 'Coadministration of boceprevir and some ritonavir-boosted protease inhibitor regimens has been associated with decreased plasma concentrations of both boceprevir and the protease inhibitors. The mechanism of interaction has not been described. Data are currently unavailable regarding a potential interaction between boceprevir and other protease inhibitors, whether alone or ritonavir-boosted.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, caution is advised if boceprevir is used in combination with ritonavir-boosted protease inhibitor regimens. Patients coinfected with chronic HCV and HIV who have been started on one of these combinations should be closely monitored for treatment response and potential HCV and HIV virologic rebound. The safety and efficacy of boceprevir has not been established in the HCV/HIV coinfected population.', 'Others', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, caution is advised if boceprevir is used in combination with ritonavir-boosted protease inhibitor regimens.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105425/', '', 'Lamivudine, Glecaprevir, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, More', 'Paritaprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Oseltamivir, Darunavir, Maribavir, Rimantadine, Elvitegravir, Telbivudine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (105307, 'Conjugated estrogens', 'Paritaprevir', 'Major', 'Theoretical concerns exist that coadministration of estrogen-containing products may increase the risk of liver enzyme elevations associated with the use of ombitasvir/paritaprevir/ritonavir, with or without dasabuvir. The proposed mechanism is the inhibition of UDP-glucuronosyltransferase (UGT) by ombitasvir, paritaprevir, and dasabuvir.', 'DDInter', 'Caution is recommended if estrogen-containing products are used in patients receiving ombitasvir/paritaprevir/ritonavir, with or without dasabuvir. Liver function tests should be performed on all patients during the first 4 weeks of treatment and as clinically indicated thereafter. If ALT is elevated above baseline at any time during treatment, the test should be repeated and monitored closely. Therapy should be discontinued if ALT levels remain persistently greater than 10 times the ULN, or if ALT elevations are accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Metabolism', 'Caution is recommended if estrogen-containing products are used in patients receiving ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105427/', '', 'Bazedoxifene', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Velpatasvir, Rilpivirine, More', 1767369485);
