INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (105323, 'Fluconazole', 'Ritonavir', 'Minor', 'The coadministration with fluconazole may slightly increase the plasma concentrations of ritonavir. The proposed mechanism is fluconazole inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ritonavir. Other azole antifungal agents are expected to affect ritonavir in a similar fashion but possibly to a greater extent, since fluconazole is a relatively weak inhibitor of CYP450 3A4. No special precautions are necessary when fluconazole is given with ritonavir.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105443/', '', 'Tetracycline, Norfloxacin, Secnidazole, Cefixime, Amikacin, Levofloxacin, Flucytosine, Anidulafungin, Micafungin, Oteseconazole, Salicylic acid, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Brincidofovir, Elbasvir, Darunavir, More', 1767369485);
