INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (105403, 'Guanfacine', 'Rifabutin', 'Major', 'Coadministration with inducers of CYP450 3A4 may significantly decrease the plasma concentrations of guanfacine, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Close monitoring and dosage adjustment should be considered when guanfacine is administered with potent and moderate CYP450 3A4 inducers. For extended-release guanfacine, some manufacturers recommend increasing the dosage up to double the recommended level when initiating therapy in patients who are already receiving a CYP450 3A4 inducer. Further dosage adjustments may be required based on patient tolerance and response. If a CYP450 3A4 inducer is added to existing guanfacine therapy, the guanfacine dosage may be increased up to double the recommended level over 1 to 2 weeks. The dosage should be decreased to the recommended level over 1 to 2 weeks following discontinuation of the CYP450 3A4 inducer.', 'Metabolism', 'Close monitoring and dosage adjustment should be considered when guanfacine is administered with potent and moderate CYP450 3A4 inducers.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105526/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Streptomycin, Trimethoprim, Cycloserine, Ethambutol, Pyridoxine, Ethionamide, More', 'Rauwolfia serpentina root, Reserpine, Methyldopa, Clonidine, Deserpidine', 1767369485);
