INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (105496, 'Rifabutin', 'Quinidine', 'Moderate', 'Rifamycins may increase the hepatic metabolism of quinidine by inducing CYP450 3A4 isoenzymes. Serum quinidine levels and quinidine effect may be decreased.', 'DDInter', 'Frequent monitoring of quinidine levels and arrhythmia control is recommended. Increased quinidine dosage may be needed. When a rifamycin is discontinued, enzyme induction and increased hepatic metabolism of quinidine may persist for one week. Patients should be advised to notify their physician if they experience an increase in irregular heartbeats.', 'Metabolism', 'Frequent monitoring of quinidine levels and arrhythmia control is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/105619/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid, More', 'Propafenone, Tocainide, Procainamide, Flecainide, Mexiletine, Bretylium, Moricizine, Ibutilide', 1767369485);
