INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (106026, 'Methotrexate', 'Lamotrigine', 'Minor', 'Lamotrigine is a weak inhibitor of dihydrofolate reductase. Theoretically, coadministration with other dihydrofolate reductase inhibitors may have additive effects on rapidly proliferating tissues such as bone marrow, possibly increasing the risk of megaloblastic anemia and other hematologic adverse effects. However, a clinically significant interaction has not been reported. During prolonged human dosing, lamotrigine did not induce significant changes in the hemoglobin concentration, mean corpuscular volume, or serum or red blood cell folate concentrations for up to one year. No significant effects on red blood cell folate concentration have been observed for up to five years.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/106149/', '', 'Brivaracetam, Ezogabine, Cenobamate, Methylphenobarbital, Ethosuximide, Lacosamide, Tiagabine, Rufinamide, Paramethadione, Perampanel, Ganaxolone, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 1767369485);
