INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (106969, 'Ranolazine', 'Pentamidine', 'Moderate', 'Ranolazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if ranolazine is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of ranolazine, the maximum recommended dosage of 1000 mg twice daily should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Ranolazine is contraindicated in patients with liver cirrhosis because of the profound effect on QT prolongation in this population.', 'Synergism', 'Caution is recommended if ranolazine is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/107092/', '', 'Alprostadil, Indomethacin, Ibuprofen, Ubidecarenone, Regadenoson', 'Miltefosine, Nifurtimox, Benznidazole', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (106970, 'Relugolix', 'Pentamidine', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/107093/', '', 'Miltefosine, Nifurtimox, Benznidazole', 'Norethisterone, Nafarelin, Octreotide, Elagolix, Cetrorelix, Estradiol, Ganirelix, Lanreotide, Gonadorelin, Anastrozole, Fulvestrant, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (106971, 'Remdesivir', 'Pentamidine', 'Moderate', 'Coadministration of remdesivir with nephrotoxic agents may increase the plasma concentrations of its main metabolite, GS-441524, and the excipient, sulfobutylether-beta-cyclodextrin sodium (SBECD). Both GS-441524 and SBECD are primarily eliminated by the kidneys, thus renal impairment secondary to the use of nephrotoxic agents may reduce their clearance and increase the risk of accumulation.', 'DDInter', 'Some authorities recommend avoiding the concomitant use of remdesivir with drugs that can reduce renal function. Caution is advised if remdesivir is used in patients who have recently received potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function (estimated glomerular filtration rate, or eGFR, for adult and pediatric patients older than 28 days; serum creatinine for full-term neonates at least 7 days up to 28 days old) should be evaluated prior to starting remdesivir and monitored daily during treatment or as clinically appropriate.', 'Excretion', 'Some authorities recommend avoiding the concomitant use of remdesivir with drugs that can reduce renal function.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/107094/', '', 'Miltefosine, Fexinidazole, Nifurtimox, Benznidazole', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (106972, 'Repaglinide', 'Pentamidine', 'Moderate', 'Pentamidine may interfere with the therapeutic effects of insulin and other antidiabetic agents. The use of pentamidine has been associated with disturbances in blood glucose homeostasis due to direct toxic effects on beta cells of the pancreas. Hypoglycemia, which may be severe and/or prolonged, as well as hyperglycemia and insulin-dependent diabetes mellitus, the latter of which may be irreversible, have been reported.', 'DDInter', 'Blood glucose should be monitored closely during and after pentamidine therapy in patients receiving insulin or other antidiabetic agents, especially if they are elderly or have renal impairment. Patients should learn to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypo- or hyperglycemia occur during pentamidine therapy, patients should initiate appropriate remedial therapy immediately and contact their physician. Dosage adjustments may be required if an interaction is suspected.', 'Antagonism', 'Blood glucose should be monitored closely during and after pentamidine therapy in patients receiving insulin or other antidiabetic agents, especially if they are elderly or have renal impairment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/107095/', '', 'Miltefosine, Nifurtimox, Benznidazole', 'Simvastatin, Guar gum, Rosuvastatin', 1767369485);
