INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (107298, 'Epirubicin', 'Perindopril', 'Moderate', 'The concomitant or sequential use of epirubicin with agents known to induce hepatotoxicity may potentiate the risk of liver injury. Epirubicin undergoes elimination through the hepatobiliary system.', 'DDInter', 'Caution and clinical and laboratory monitoring for signs of liver injury are advisable before, during, or after use of epirubicin with other agents that are potentially hepatotoxic.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/107421/', '', 'Idarubicin, Valrubicin, Plicamycin, Ixabepilone, Mitoxantrone, Bleomycin, Daunorubicin, Dactinomycin', 'Felodipine, Amlodipine, Verapamil, Bisoprolol, Benazepril, Hydrochlorothiazide, Indapamide, Amlodipine, Valsartan, Lovastatin, Acetylsalicylic acid, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (107299, 'Eplerenone', 'Perindopril', 'Moderate', 'Coadministration of eplerenone with ACE inhibitors or angiotensin II receptor antagonists may increase the risk of hyperkalemia due to additive pharmacodynamic effects.', 'DDInter', 'Given the potential for serious, sometimes fatal cardiac arrhythmias associated with hyperkalemia, caution is advised if eplerenone is administered with ACE inhibitors or angiotensin II receptor antagonists. Periodic monitoring of serum potassium levels is recommended until the effect of eplerenone is established. In clinical trials, monitoring occurred every 2 weeks for the first 1 to 2 months, then monthly thereafter. Particular caution is warranted in patients with renal impairment, diabetes, old age, or dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', 'Given the potential for serious, sometimes fatal cardiac arrhythmias associated with hyperkalemia, caution is advised if eplerenone is administered with ACE inhibitors or angiotensin II receptor antagonists.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/107422/', '', 'Felodipine, Indapamide, Lovastatin, Bempedoic acid, Indapamide', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (107300, 'Eprosartan', 'Perindopril', 'Major', 'Coadministration of an ACE inhibitor in combination with an angiotensin II receptor antagonist may increase the risk of hyperkalemia, hypotension, syncope, and renal dysfunction due to additive or synergistic effects on the renin-angiotensin system.', 'DDInter', 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy. Routine monitoring of electrolytes and renal function may be indicated in the elderly or patients with worsening heart failure or a risk for dehydration. Potassium supplementation should generally be avoided unless it is closely monitored, and patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/107423/', '', 'Felodipine, Amlodipine, Verapamil, Bisoprolol, Hydrochlorothiazide, Indapamide, Amlodipine, Valsartan, Simvastatin, Atorvastatin, Lovastatin, More', 'Nebivolol', 1767369485);
