INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (107313, 'Sodium aurothiomalate', 'Perindopril', 'Moderate', 'Concomitant ACE inhibitor therapy may potentiate the risk of nitritoid (vasomotor) reactions associated with injectable gold therapy. The mechanism of interaction is unknown, although investigators suggest that ACE inhibitors may unmask drug hypersensitivity reactions by preventing the breakdown of bradykinins.', 'DDInter', 'Patients on injectable gold therapy in combination with an ACE inhibitor should be monitored for symptoms of nitritoid reactions such as facial flushing, nausea, vomiting, and hypotension following an injection. Elderly patients in particular should be observed following treatment. Oxygen, intravenous fluids, epinephrine, steroids, and other supportive therapy may be necessary for treatment.', 'Others', 'Patients on injectable gold therapy in combination with an ACE inhibitor should be monitored for symptoms of nitritoid reactions such as facial flushing, nausea, vomiting, and hypotension following an injection.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/107437/', '', 'Felodipine, Amlodipine, Verapamil, Bisoprolol, Hydrochlorothiazide, Indapamide, Amlodipine, Valsartan, Niacin, Acetylsalicylic acid, Ezetimibe, More', 'Auranofin, Penicillamine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (107314, 'Heparin', 'Perindopril', 'Moderate', 'The concomitant use of angiotensin converting enzyme (ACE) inhibitors or angiotensin receptor blockers (ARBs) and heparin or low molecular weight heparins may increase the risk of hyperkalemia. ACE inhibitors, ARBs, and some heparins individually have been associated with increased potassium levels or hyperkalemia.', 'DDInter', 'Caution is advised if ACE inhibitors or ARBs are used with heparin or low molecular weight heparin. Serum potassium and renal function should be checked regularly.', 'Synergism', 'Caution is advised if ACE inhibitors or ARBs are used with heparin or low molecular weight heparin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/107438/', '', 'Bempedoic acid', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Cangrelor, Caplacizumab, Argatroban, Prasugrel, Desirudin, More', 1767369485);
