INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (108600, 'Sevoflurane', 'Promethazine', 'Moderate', 'Sevoflurane may cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if sevoflurane is used in combination with other drugs that can prolong the QT interval. The patient''s cardiovascular status and serum electrolytes should be closely monitored.', 'Synergism', 'Caution is recommended if sevoflurane is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/108724/', '', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, More', 'Isoflurane, Nitrous oxide, Desflurane, Enflurane, Etomidate', 1767369485);
