INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (108705, 'Entrectinib', 'Dextropropoxyphene', 'Moderate', 'Entrectinib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of entrectinib with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Patients should have electrocardiograms (ECGs) performed prior to initiation of entrectinib and periodically during treatment as appropriate based on individual risk factors.', 'Synergism', 'Coadministration of entrectinib with other drugs that can prolong the QT interval should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/108829/', '', 'Codeine, Morphine, Oxymorphone, Tapentadol, Meperidine, Butorphanol, Acetaminophen, Hydromorphone, Dezocine, Opium, Ibuprofen, More', 'Imatinib, Dabrafenib, Acalabrutinib, Pexidartinib, Tepotinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Cobimetinib, Capmatinib, More', 1767369485);
