INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109001, 'Dextropropoxyphene', 'Ticagrelor', 'Moderate', 'Coadministration with opioid agonists may delay and reduce the absorption of orally administered P2Y12 inhibitors (e.g., clopidogrel, prasugrel, ticagrelor). The proposed mechanism may involve opioid-mediated slowed gastric emptying.', 'DDInter', 'Although data are limited, caution is recommended when orally administered P2Y12 inhibitors are given concomitantly with opioid agonists. In acute coronary syndrome patients who require an opioid agonist, the use of a parenteral antiplatelet agent, such as cangrelor, should be considered.', 'Absorption', 'Although data are limited, caution is recommended when orally administered P2Y12 inhibitors are given concomitantly with opioid agonists.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109125/', '', 'Acetaminophen, Naloxone, Naltrexone', 'Betrixaban, Apixaban, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109002, 'Dextropropoxyphene', 'Timolol', 'Moderate', 'Propoxyphene may increase the serum levels of some oral beta-blockers. The proposed mechanism is inhibition of CYP450 2D6 first-pass metabolism and decreased hepatic clearance. Data are available for metoprolol and propranolol only; however, other hepatically metabolized beta-blockers may also be affected. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact.', 'DDInter', 'Patients receiving this combination should be monitored for hypotension, heart failure, bradycardia, arrhythmias, and mental status changes when propoxyphene is added to the patient''s medical regimen, and for decreased beta-blockade when propoxyphene is deleted from the regimen. A reduction in beta-blocker dosage may necessary.', 'Metabolism', 'Patients receiving this combination should be monitored for hypotension, heart failure, bradycardia, arrhythmias, and mental status changes when propoxyphene is added to the patient''s medical regimen, and for decreased beta-blockade when propoxyphene is deleted from the regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109126/', '', 'Carteolol, Latanoprost, Methazolamide, Neostigmine, Pilocarpine, Physostigmine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, Carteolol, More', 'Naloxone, Naltrexone, Tapentadol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109003, 'Dextropropoxyphene', 'Tizanidine', 'Major', 'Sedatives, tranquilizers, muscle relaxants, antidepressants, and other central nervous system (CNS) depressants may have additive CNS- and/or respiratory-depressant effects with propoxyphene. Misuse of propoxyphene, either alone or in combination with other CNS depressants, has been a major cause of drug-related deaths, particularly in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse. Pharmacokinetically, propoxyphene is an inhibitor of CYP450 2D6 and may increase the plasma concentrations of many psychotropic agents that are metabolized by the isoenzyme such as phenothiazines, haloperidol, risperidone, phenobarbital, and some tricyclic antidepressants and serotonin reuptake inhibitors.', 'DDInter', 'Caution is advised if propoxyphene is used with sedatives, tranquilizers, muscle relaxants, antidepressants, and other CNS depressants, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse. Dosage reductions may be appropriate. Patients should be monitored for potentially excessive or prolonged CNS and respiratory depression and other CNS adverse effects. Patients should be warned not to exceed recommended dosages, to avoid alcohol, and to avoid activities requiring mental alertness until they know how these agents affect them.', 'Metabolism, Synergism', 'Caution is advised if propoxyphene is used with sedatives, tranquilizers, muscle relaxants, antidepressants, and other CNS depressants, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109127/', '', 'Naloxone', '', 1767369485);
