INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109303, 'Dalfopristin', 'Brigatinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of brigatinib, which is partially metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of moderate CYP450 3A4 inhibitors should generally be avoided during therapy with brigatinib. If coadministration is required, the brigatinib dose should be reduced by approximately 40% (i.e., from 180 mg to 120 mg, 120 mg to 90 mg, or from 90 mg to 60 mg). There are authorities (AU and UK) that state that no dose adjustment is required, however, patients should be closely monitored. Patients should be monitored for increased brigatinib adverse reactions such as nausea, vomiting, diarrhea, hypertension, bradycardia, hyperglycaemia, visual disturbances, lymphopenia, anemia, and elevations in pancreatic enzymes and creatine phosphokinase. If a dose adjustment is made, the brigatinib dose that was tolerated previously may be resumed following discontinuation of the CYP450 3A4 inhibitor.', 'Metabolism', 'Concomitant use of moderate CYP450 3A4 inhibitors should generally be avoided during therapy with brigatinib.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109427/', '', 'Dabrafenib, Dacomitinib, Vemurafenib, Vandetanib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, Erlotinib, More', '', 1767369485);
