INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109450, 'Dalfopristin', 'Modafinil', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme. Conversely, the plasma levels of some of these inhibitors may decrease, since many of them are also substrates of CYP450 3A4, and modafinil and armodafinil have been found to be modest inducers of CYP450 3A4. The clinical significance of this potential interaction is unknown. Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109574/', '', 'Amphetamine, Pemoline, Atomoxetine, Metamfetamine, Lisdexamfetamine, Solriamfetol, Caffeine, Methylphenidate, Dexmethylphenidate, Dextroamphetamine, Citicoline', '', 1767369485);
