INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109492, 'Dalfopristin', 'Quinidine', 'Major', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme, including certain agents that can cause prolongation of the QTc interval (e.g., cisapride, disopyramide, pimozide, quinidine, tacrolimus). High plasma levels of these agents have been rarely associated with ventricular arrhythmias including ventricular tachycardia, ventricular fibrillation, and torsade de pointes, as well as cardiac arrest and sudden death.', 'DDInter', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of agents that may prolong the QTc interval, concomitant use with dalfopristin-quinupristin should be avoided if they are known to be substrates of CYP450 3A4.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109616/', '', 'Tocainide, Procainamide, Flecainide, Mexiletine, Bretylium, Moricizine, Ibutilide', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109493, 'Dalfopristin', 'Quinine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of quinine, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised if quinine is used in combination with potent and moderate CYP450 3A4 inhibitors. Patients should be monitored closely for adverse reactions associated with quinine such as hematologic toxicities and cardiac arrhythmias including torsade de pointes and atrial fibrillation. Patients should be advised to contact their physician if they experience increased side effects such as headache, flushing, sweating, nausea, vomiting, diarrhea, abdominal pain, tinnitus, dizziness, vertigo, hearing impairment, blurred vision, vision impairment, and irregular heart rhythm.', 'Metabolism', 'Caution is advised if quinine is used in combination with potent and moderate CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109617/', '', 'Risdiplam, Collagenase clostridium histolyticum, Onasemnogene abeparvovec, Hyaluronic acid, Artesunate, Mefloquine, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109494, 'Dalfopristin', 'Ranolazine', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ranolazine, which is primarily metabolized by the isoenzyme. Because ranolazine prolongs QT interval in a dose-dependent manner, high plasma levels of ranolazine may increase the risk of ventricular arrhythmias such as ventricular tachycardia, ventricular fibrillation, and torsade de pointes.', 'DDInter', 'The dosage of ranolazine should not exceed 500 mg twice a day when coadministered with moderate inhibitors of CYP450 3A4, including but not limited to diltiazem, verapamil, aprepitant, erythromycin, fluconazole, and grapefruit juice. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', 'The dosage of ranolazine should not exceed 500 mg twice a day when coadministered with moderate inhibitors of CYP450 3A4, including but not limited to diltiazem, verapamil, aprepitant, erythromycin, fluconazole, and grapefruit juice.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109618/', '', 'Alprostadil, Indomethacin, Ibuprofen, Ubidecarenone, Adenosine, Regadenoson', '', 1767369485);
