INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109497, 'Dalfopristin', 'Ribociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and risk of adverse effects of ribociclib, which is a substrate of the isoenzyme. Administration of erythromycin, a moderate CYP450 3A4 inhibitor, is predicted to increase ribociclib Cmax and AUC by 1.3- and 1.9-fold, respectively.', 'DDInter', 'Caution is advised when ribociclib is used with CYP450 3A4 inhibitors. Dosage adjustment for ribociclib is not generally considered necessary for suspected interactions with mild to moderate inhibitors. However, some manufacturers of ribociclib recommend that concomitant use with moderate CYP450 3A4 inhibitor verapamil should be avoided (AU, UK). Patients should be monitored for the development of ribociclib-related adverse effects such as infections, neutropenia, leucopenia, anemia, thrombocytopenia, nausea, vomiting, diarrhea, stomatitis, anorexia, alopecia, fatigue, headache, and abnormal liver function.', 'Metabolism', 'Caution is advised when ribociclib is used with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109621/', '', 'Dabrafenib, Dacomitinib, Vemurafenib, Vandetanib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, Erlotinib, More', '', 1767369485);
