INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109601, 'Sevelamer', 'Ofloxacin', 'Moderate', 'INTERVAL: Concurrent administration of sevelamer hydrochloride may decrease the oral bioavailability of ciprofloxacin and other quinolones, potentially reducing antibiotic efficacy and increasing the risk of bacterial resistance. The proposed mechanism is reduced quinolone absorption due to chelation with the protonated amine groups of sevelamer in the intestine.', 'DDInter', 'Oral quinolone antibiotics should not be administered simultaneously with sevelamer hydrochloride. The optimal length of time needed to separate ingestion of these drugs is currently unknown. Based on the fact that it took up to 4 hours to achieve ciprofloxacin Cmax in all 15 volunteers of the study, the authors suggested a separation time of at least 4 hours between sevelamer and ciprofloxacin administration to reduce the likelihood of a significant interaction, particularly in patients with end-stage renal disease whose gastrointestinal motility may be slowed.', 'Absorption', 'Oral quinolone antibiotics should not be administered simultaneously with sevelamer hydrochloride.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109726/', '', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, Arginine, More', 'Tinidazole, Tetracycline, Cefepime, Cefuroxime, Secnidazole, Cefixime, Azithromycin, Amikacin, Rifamycin, Kanamycin, Ampicillin, More', 1767369485);
