INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109690, 'Chlorpromazine', 'Ropinirole', 'Moderate', 'Agents with central antidopaminergic activity such as phenothiazines, neuroleptics, and certain antiemetics may antagonize the pharmacologic effects of dopaminergic drugs, and vice versa. In addition, the central nervous system depressant and hypotensive effects of these agents may be additively or synergistically increased when taken together.', 'DDInter', 'Concomitant use of dopaminergic drugs with antidopaminergic agents should generally be avoided. If coadministration is necessary, patients should be alerted to the possibility of excessive drowsiness and monitored for potentially diminished therapeutic response to both treatments. Patients treated for Parkinson''s disease should generally avoid antidopaminergic agents, particularly phenothiazines and older neuroleptic agents, since these agents may cause extrapyramidal reactions and exacerbate the symptoms of Parkinson''s disease. Likewise, patients with a major psychotic disorder should ordinarily not be treated with dopaminergic drugs because of the risk of exacerbating the psychosis with an increase in central dopaminergic tone.', 'Synergism', 'Concomitant use of dopaminergic drugs with antidopaminergic agents should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109815/', '', 'Pimavanserin', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109691, 'Conjugated estrogens (topical)', 'Ropinirole', 'Moderate', 'Coadministration with estrogen-containing drugs may increase the plasma concentrations of ropinirole. Data from a population pharmacokinetic study revealed that estrogens, mainly ethinyl estradiol at 0.6 to 3 mg over a 4-month to 23-year period, reduced the oral clearance of ropinirole by up to 36% in 16 patients. The mechanism of interaction has not been described.', 'DDInter', 'Dosage adjustment for ropinirole should not be necessary in patients already on estrogen therapy because patients must be carefully titrated to tolerance or adequate effect. However, if estrogen therapy is initiated or discontinued during treatment with ropinirole, then patients should be monitored for altered ropinirole effects and the dosage adjusted as necessary. Patients should be advised to notify their physician if they experience agitation, orthostasis, sedation, confusion, hallucinations, and/or increased dyskinesia, flushing, dry mouth, sweating, or heart rate.', 'Others', 'Dosage adjustment for ropinirole should not be necessary in patients already on estrogen therapy because patients must be carefully titrated to tolerance or adequate effect.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109816/', '', 'Apomorphine, Opicapone, Cabergoline, Amantadine, Rasagiline, Entacapone, Selegiline, Tolcapone, Rotigotine, Bromocriptine, Pergolide, More', 'Bazedoxifene', 1767369485);
