INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109873, 'Zalcitabine', 'Ribavirin', 'Moderate', 'Concomitant use of oral ribavirin and nucleoside reverse transcriptase inhibitors (NRTIs) may increase the risk of symptomatic hyperlactatemia and lactic acidosis. Ribavirin itself is a nucleoside analogue, and these agents alone and in combination have been associated with the syndrome of lactic acidosis and severe hepatomegaly with steatosis. Other mitochondrial toxicities associated with the use of nucleoside analogues include lipodystrophy, peripheral neuropathy, and pancreatitis.', 'DDInter', 'Coadministration of ribavirin and NRTIs should be undertaken with caution and only if the benefit is anticipated to outweigh the potential risks. Patients should be advised to promptly seek medical attention if they experience symptoms of toxicity such as nausea, vomiting, abdominal pain/distention, fatigue, unexplained weight loss, tachypnea, dyspnea, motor weakness, numbness, tingling, and pain in hands and feet.', 'Synergism', 'Coadministration of ribavirin and NRTIs should be undertaken with caution and only if the benefit is anticipated to outweigh the potential risks.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109998/', '', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', 'Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (109874, 'Isoniazid', 'Rifampicin', 'Minor', 'The risk of hepatotoxicity is greater when rifampin and isoniazid are given concomitantly than when either drug is given alone. Rifampin appears to alter the metabolism of isoniazid and increase the amount of toxic metabolites. Theoretically, a similar reaction may occur with rifabutin and isoniazid. Patients who are elderly, have hepatic impairment, are slow acetylators of isoniazid, drink alcohol daily, are female, or are taking other strong CYP450-inducing agents may be at greater risk of hepatotoxicity.', 'DDInter', 'Close monthly monitoring for clinical or laboratory evidence of altered hepatic function is recommended. Patients should be advised to promptly report early symptoms of hepatitis such as fatigue, weakness, malaise, anorexia, nausea, or vomiting. Discontinuation of either or both drugs may be necessary.', 'Metabolism', 'Close monthly monitoring for clinical or laboratory evidence of altered hepatic function is recommended.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109999/', '', 'Capreomycin, Rifamycin, Rifapentine, Rifampicin, Cycloserine, Rifabutin, Pyridoxine', 'Capreomycin, Rifamycin, Sulfamethoxazole, Rifapentine, Trimethoprim, Isoniazid, Pyridoxine, Pretomanid', 1767369485);
