INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110203, 'Risperidone', 'Lapatinib', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110328/', '', 'Brexpiprazole, Loxapine, Molindone, Thiothixene', 'Dabrafenib, Acalabrutinib, Pexidartinib, Avapritinib, Bosutinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110204, 'Risperidone', 'Larotrectinib', 'Moderate', 'Coadministration with larotrectinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.', 'DDInter', 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever larotrectinib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. Some authorities recommend avoiding concomitant use of larotrectinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110329/', '', 'Chlorpromazine, Mesoridazine, Methotrimeprazine, Promazine, Fluphenazine, Prochlorperazine, Asenapine, Thiothixene, Thioridazine, Clozapine, Perphenazine, More', 'Dabrafenib, Acalabrutinib, Pexidartinib, Avapritinib, Bosutinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, More', 1767369485);
