INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110361, 'Rivastigmine', 'Risperidone', 'Moderate', 'Due to opposing effects, agents that possess anticholinergic activity (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; class IA antiarrhythmics especially disopyramide; carbamazepine; cimetidine; ranitidine) may negate the already small pharmacologic benefits of acetylcholinesterase inhibitors in the treatment of dementia. These agents may also adversely affect elderly patients in general. Clinically significant mental status changes associated with anticholinergic agents can range from mild cognitive impairment to delirium, and patients with Alzheimer''s disease and other dementia are especially sensitive.', 'DDInter', 'Drugs that possess anticholinergic activity should generally be avoided in patients with Alzheimer''s disease or other cognitive impairment, regardless of whether they are receiving an acetylcholinesterase inhibitor. For patients requiring treatment to counteract adverse effects of acetylcholinesterase inhibitor therapy (e.g., gastrointestinal intolerance, urinary problems), an agent without anticholinergic properties should be used whenever possible. Otherwise, a dosage reduction, slower titration, or even discontinuation of the acetylcholinesterase inhibitor should be considered. For patients who are already receiving an acetylcholinesterase inhibitor with anticholinergic agents, every attempt should be made to discontinue the latter or substitute them with less anticholinergic alternatives.', 'Antagonism', 'Drugs that possess anticholinergic activity should generally be avoided in patients with Alzheimer''s disease or other cognitive impairment, regardless of whether they are receiving an acetylcholinesterase inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110486/', '', 'Amisulpride, Cariprazine, Lurasidone', 'Aducanumab', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110362, 'Rolapitant', 'Risperidone', 'Moderate', 'Coadministration with rolapitant may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. Rolapitant is a moderate CYP450 2D6 inhibitor, with inhibitory effect lasting at least 7 days after a single dose.', 'DDInter', 'Caution is advised when rolapitant is prescribed with drugs that are significantly metabolized by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever rolapitant is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when rolapitant is prescribed with drugs that are significantly metabolized by CYP450 2D6, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110487/', '', 'Quetiapine, Asenapine, Promazine, Prochlorperazine, Thiothixene, Pimavanserin, Lumateperone, Ziprasidone, Olanzapine, Trifluoperazine, Amisulpride, More', 'Aprepitant', 1767369485);
