INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110482, 'Rituximab', 'Natalizumab', 'Major', 'Concomitant or recent use of immunosuppressant, immunomodulating, or antineoplastic agents in patients treated with natalizumab may increase the risk of infections including progressive multifocal leukoencephalopathy (PML), a severely disabling, potentially fatal opportunistic viral infection of the brain.', 'DDInter', 'The safety and efficacy of natalizumab in combination with immunosuppressant, immunomodulating, antineoplastic or other myelosuppressive agents have not been established. In general, patients receiving chronic therapy with such agents should not be treated with natalizumab due to potentially increased risk of PML and other serious infections. Typical symptoms associated with PML are diverse, progress over days to weeks, and include progressive weakness on one side of the body, clumsiness of limbs, disturbance of vision, and changes in thinking, memory, and orientation leading to confusion and personality changes. Due to the long half-life of natalizumab, immune effects are possible for up to 2 to 3 months following its discontinuation.', 'Synergism', 'The safety and efficacy of natalizumab in combination with immunosuppressant, immunomodulating, antineoplastic or other myelosuppressive agents have not been established.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110607/', '', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Secukinumab, Ravulizumab, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, Apremilast, Inebilizumab, Belumosudil, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110483, 'Rituximab', 'Niraparib', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with niraparib. Myelodysplastic syndrome (MDS) and acute myeloid leukemia (AML) have been reported in patients treated with niraparib.', 'DDInter', 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110608/', '', 'Pertuzumab, Cetuximab, Olaratumab, Avelumab, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', 'Pertuzumab, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110484, 'Rituximab', 'Ocrelizumab', 'Moderate', 'The use of the CD20-directed cytolytic antibody ocrelizumab with other immune-modulating or immunosuppressive therapy, including immunosuppressant doses of corticosteroids may increase the risk of infections.', 'DDInter', 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy. In addition, the prolonged immunosuppressant effects and mode of action of other immunosuppressant drugs such as daclizumab, fingolimod, natalizumab, teriflunomide, or mitoxantrone should be considered prior to starting ocrelizumab therapy. Patients should be advised to notify their doctor if they develop signs or symptoms of infection, including upper or lower respiratory tract infection, skin infection, herpes related infection, or PML.', 'Synergism', 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110609/', '', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Secukinumab, Ravulizumab, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, Apremilast, Inebilizumab, Belumosudil, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110485, 'Rituximab', 'Olaparib', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with olaparib. Myelodysplastic syndrome (MDS) and acute myeloid leukemia (AML) have been reported in patients treated with olaparib.', 'DDInter', 'Caution is advised if olaparib is prescribed with other myelosuppressive agents. Complete blood counts should be performed at baseline and monthly thereafter. Do not start olaparib until patients have recovered from hematologic toxicity caused by previous chemotherapy.', 'Synergism', 'Caution is advised if olaparib is prescribed with other myelosuppressive agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110610/', '', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', 'Pertuzumab, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110486, 'Rituximab', 'Omacetaxine mepesuccinate', 'Moderate', 'The use of omacetaxine with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Omacetaxine alone may cause severe myelosuppression, neutropenia, lymphopenia, and opportunistic infections. The risk may theoretically increase when coadministered with other hematotoxic therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy. Patients should be advised to seek medical attention if they experience symptoms such as fever, chills, shortness of breath, fatigue, and any unusual bleeding or bruising.', 'Synergism', 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110611/', '', 'Pertuzumab, Avelumab, Panitumumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Nivolumab, Pembrolizumab, Cemiplimab', 'Pertuzumab, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110487, 'Rituximab', 'Ozanimod', 'Major', 'Coadministration of ozanimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may increase the risk of unintended additive immunosuppressive effects. Ozanimod causes reversible sequestration of lymphocytes in lymphoid tissues. The safety and efficacy of ozanimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated.', 'DDInter', 'Concomitant use of ozanimod with antineoplastic, immunosuppressive, or immune-modulating agents should be avoided according to some authorities (UK); however, other authorities (AU, US) advise caution and close monitoring during coadministration and for up to 3 months after the last dose of ozanimod. When switching from drugs with prolonged immune effects to ozanimod, the half-life and mode of action of these drugs must be considered to avoid unintended additive immunosuppressive effects while at the same time minimizing risk of disease reactivation.', 'Synergism', 'Concomitant use of ozanimod with antineoplastic, immunosuppressive, or immune-modulating agents should be avoided according to some authorities (UK); however, other authorities (AU, US) advise caution and close monitoring during coadministration and for up to 3 months after the last dose of ozanimod.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110612/', '', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Secukinumab, Ravulizumab, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, Apremilast, Inebilizumab, Belumosudil, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110488, 'Rituximab', 'Palifermin', 'Moderate', 'INTERVAL: In a clinical trial, administration of palifermin within 24 hours of chemotherapy resulted in increased severity and duration of oral mucositis, presumably due to increased sensitivity of the rapidly dividing epithelial cells in the immediate post-chemotherapy period.', 'DDInter', 'Palifermin should not be administered within 24 hours before, during infusion of, or within 24 hours after administration of myelotoxic chemotherapy.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110613/', '', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Dostarlimab, Amivantamab, Ramucirumab, Atezolizumab, Pembrolizumab, Sacituzumab govitecan, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110489, 'Rituximab', 'Pegfilgrastim', 'Moderate', 'INTERVAL: The safety and efficacy of hematopoietic growth factors such as colony-stimulating factors (G-CSF and GM-CSF) and stem cell factors (SCF) given simultaneously with cancer chemotherapy have not been established. Theoretical concerns exist regarding their concomitant administration because hematopoietic growth factors stimulate myeloid cell proliferation while antineoplastic agents primarily target rapidly dividing cells.', 'DDInter', 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, the manufacturer recommends that pegfilgrastim not be given from 14 days before until 24 hours after administration of antineoplastic agents.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110614/', '', 'Interferon alfa-n1, Oprelvekin, Elapegademase, Interferon beta-1b, Peginterferon alfa-2a, Peginterferon beta-1a, Interferon alfacon-1, Interferon beta-1a, Histamine, Aldesleukin, Pegademase, More', 'Pertuzumab, Cetuximab, Olaratumab, Avelumab, Elotuzumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Dostarlimab, Amivantamab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110490, 'Rituximab', 'Radium Ra 223 dichloride', 'Moderate', 'The concomitant use of bone marrow depressants and radium Ra 223 dichloride may have additive myelosuppressive effects.', 'DDInter', 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride. Dose reductions of the other bone marrow depressants may be necessary. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms. The safety and efficacy of concomitant use of chemotherapy have not been established.', 'Synergism', 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110615/', '', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Dostarlimab, Amivantamab, Ramucirumab, Atezolizumab, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110491, 'Rituximab', 'Rilonacept', 'Moderate', 'The use of interleukin-1 blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Interleukin-1 blockade alone may cause neutropenia and serious infections, and the risk may be increased in the presence of other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids.', 'DDInter', 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established. Until more information is available, interleukin-1 blockers should preferably not be administered to immunosuppressed patients, or they should be prescribed with extreme caution.', 'Synergism', 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110616/', '', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Secukinumab, Ravulizumab, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, Apremilast, Inebilizumab, Belumosudil, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110492, 'Roflumilast', 'Rituximab', 'Moderate', 'The concomitant use of roflumilast with immunosuppressive agents has not been adequately studied. Specifically, roflumilast and its active metabolite, roflumilast N-oxide, suppress the release of inflammatory mediators including leukotriene B4, reactive oxygen species, tumor necrosis factor alpha, interferon gamma, and granzyme B. Roflumilast also reduces sputum neutrophils and attenuates influx of neutrophils and eosinophils into the airways.', 'DDInter', 'According to the product labeling for roflumilast in some countries , concomitant use with immunosuppressive agents such as methotrexate, azathioprine, infliximab, etanercept, and systemic corticosteroids (except when used short-term, e.g., in the treatment of COPD exacerbations) should be avoided if possible. Due to a lack of clinical experience, treatment with roflumilast should not be initiated, or existing treatment should be stopped, in patients receiving immunosuppressive agents and in patients with severe acute infectious diseases, cancers (except basal cell carcinoma), or severe immunological diseases (e.g., HIV infection, multiple sclerosis, lupus erythematosus, progressive multifocal leukoencephalopathy).', 'Synergism', 'According to the product labeling for roflumilast in some countries , concomitant use with immunosuppressive agents such as methotrexate, azathioprine, infliximab, etanercept, and systemic corticosteroids (except when used short-term, e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110617/', '', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', 'Theophylline, Omalizumab, Tezepelumab, Mercaptopurine, Aminophylline, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Oxtriphylline, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110493, 'Rotavirus vaccine', 'Rituximab', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110618/', '', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110494, 'Samarium (153Sm) lexidronam', 'Rituximab', 'Major', 'Theoretical concerns exist that chemotherapeutic agents and other bone marrow depressants may potentiate the myelosuppressive effects of samarium sm 153 lexidronam.', 'DDInter', 'The manufacturer recommends avoiding concomitant use of samarium sm 153 lexidronam with chemotherapy or external beam radiation therapy unless benefits are anticipated to outweigh the risks. Moreover, samarium sm 153 lexidronam should not be given after either of these treatments until there has been time for adequate marrow recovery. Caution and close monitoring of bone marrow function are advisable if coadministration with other myelotoxic agents is required. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, unusual bleeding or bruising, or signs of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination.', 'Synergism', 'The manufacturer recommends avoiding concomitant use of samarium sm 153 lexidronam with chemotherapy or external beam radiation therapy unless benefits are anticipated to outweigh the risks.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110619/', '', 'Pertuzumab, Cetuximab, Olaratumab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Dostarlimab, Amivantamab, Ramucirumab, More', '', 1767369485);
