INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110927, 'Tolvaptan', 'Ponatinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the oral bioavailability of tolvaptan, which has been shown in vitro to be a substrate of the efflux transporter.', 'DDInter', 'Caution is advised if tolvaptan is prescribed in combination with P-glycoprotein inhibitors, especially potent ones such as amiodarone, cyclosporine, or quinidine. Serum sodium concentration and neurologic status should be closely monitored, and the tolvaptan dosage adjusted accordingly. Too rapid correction of hyponatremia increases the risk of osmotic demyelination syndrome, which is associated with dysarthria, mutism, dysphagia, lethargy, affective changes, spastic quadriparesis, seizures, coma, and death. Discontinuation or interruption of tolvaptan therapy should be considered in patients who develop too rapid a rise in serum sodium.', 'Absorption', 'Caution is advised if tolvaptan is prescribed in combination with P-glycoprotein inhibitors, especially potent ones such as amiodarone, cyclosporine, or quinidine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/111057/', '', 'Entrectinib, Acalabrutinib, Dacomitinib, Avapritinib, Selumetinib, Vandetanib, Tivozanib, Zanubrutinib, Axitinib, Sunitinib, Regorafenib, More', '', 1767369485);
