INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (110996, 'Vinblastine', 'Tolvaptan', 'Moderate', 'Coadministration with tolvaptan may increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp). The proposed mechanism is decreased clearance due to inhibition of P-glycoprotein-mediated drug efflux in the intestine, liver, and/or kidney by tolvaptan.', 'DDInter', 'Caution is advised when tolvaptan is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate. Alternatives should be considered whenever possible. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate following the initiation or discontinuation of tolvaptan. The prescribing information for the coadministered drug should be consulted for specific dosing recommendations.', 'Distribution', 'Caution is advised when tolvaptan is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/111126/', '', 'Teniposide, Irinotecan, Cabazitaxel', '', 1767369485);
