INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (111273, 'Erdafitinib', 'Sodium glycerophosphate', 'Major', 'Coadministration with agents that can alter serum phosphate levels may affect the initial dosage determination of erdafitinib. The mechanism appears to be related to the pharmacodynamic effects of fibroblast growth factor receptor (FGFR) inhibition by erdafitinib. Inhibition of the FGFR receptor has been shown to lead to an increase in serum phosphate levels.', 'DDInter', 'The manufacturer recommends that concomitant use of erdafitinib with agents that may alter serum phosphate levels be avoided before its initial dose increase period, which is between days 14 to 21 of starting erdafitinib therapy and is based on serum phosphate levels. After the initial dose increase period, an oral phosphate binder may be appropriate in the management of patients with serum phosphate levels greater than 7 mg/dL. If concomitant use with agents known to increase serum phosphate levels is required after this initial dose increase period, close monitoring of patient tolerability and serum phosphate levels is recommended.', 'Others', 'The manufacturer recommends that concomitant use of erdafitinib with agents that may alter serum phosphate levels be avoided before its initial dose increase period, which is between days 14 to 21 of starting erdafitinib therapy and is based on serum phosphate levels.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/111403/', '', 'Calcium chloride, Potassium chloride, Sodium acetate, Zinc sulfate, Arginine, Zinc chloride, Tromethamine, Sodium bicarbonate, Sodium chloride, Potassium acetate, Ammonium chloride', 'Entrectinib, Dabrafenib, Dacomitinib, Pexidartinib, Fedratinib, Avapritinib, Larotrectinib, Vemurafenib, Lapatinib, Cobimetinib, Duvelisib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (111274, 'Amyl Nitrite', 'Nitrous acid', 'Major', 'Sodium nitrite can cause methemoglobin formation, which diminishes oxygen-carrying capacity of the blood. Coadministration with other agents that are also associated with methemoglobinemia including local anesthetics (e.g., benzocaine, lidocaine, prilocaine), antimalarials (e.g., chloroquine, primaquine, quinine, tafenoquine), nitrates and nitrites, sulfonamides, acetaminophen, aminosalicylic acid, dapsone, dimethyl sulfoxide, flutamide, metoclopramide (primarily in infants), nitrofurantoin (primarily in infants), phenazopyridine, phenobarbital, phenytoin, and rasburicase may increase the risk.', 'DDInter', 'Sodium nitrite should be used with caution in the presence of other methemoglobin-inducing drugs. Patients should be closely monitored to ensure adequate perfusion and oxygenation during treatment with sodium nitrite. Methemoglobin levels should be monitored and oxygen administered whenever possible.', 'Synergism', 'Sodium nitrite should be used with caution in the presence of other methemoglobin-inducing drugs.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/111404/', '', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, Methionine, More', 1767369485);
