INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (111437, 'Sodium sulfate', 'Salbutamol', 'Moderate', 'The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/111568/', '', 'Formoterol, Cromoglicic acid, Ipratropium, Isoprenaline, Tiotropium, Beclomethasone dipropionate, Budesonide, Glycopyrronium, Aclidinium, Mometasone, Fluticasone, More', 'Alvimopan, Sorbitol, Magnesium sulfate, Methylnaltrexone, Magnesium oxide, Naloxegol, Plecanatide, Lubiprostone, Methylcellulose, Naldemedine, Prucalopride, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (111438, 'Aldesleukin', 'Sodium sulfate', 'Moderate', 'The risk of seizures induced by the use of bowel cleansing preparations may be increased in patients on concomitant medications that can lower the seizure threshold such as selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), systemic steroids, carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, and theophylline. Rare cases of generalized tonic-clonic seizures and/or loss of consciousness in association with low serum osmolality and electrolyte abnormalities (e.g., hyponatremia, hypokalemia, hypocalcemia, hypomagnesemia) have been reported with the use of bowel cleansing products in patients with no prior history of seizures. The condition resolved with correction of fluid and electrolyte abnormalities.', 'DDInter', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold. Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities. Baseline and postprocedure labs including serum electrolytes, calcium, phosphate, BUN, and creatinine should be considered, particularly in the elderly. Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process. Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia.', 'Antagonism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/111569/', '', 'Oprelvekin, Sipuleucel-T, Elapegademase, Ropeginterferon alfa-2b, Sargramostim, Histamine, Pegademase, Pegfilgrastim, Filgrastim, Glatiramer', 'Alvimopan, Sorbitol, Tegaserod, Naloxegol, Bisacodyl, Naldemedine, Lactulose, Methylnaltrexone, Lactitol, Magnesium oxide, Prucalopride, More', 1767369485);
