INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (111927, 'Velpatasvir', 'Vemurafenib', 'Moderate', 'Coadministration with vemurafenib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 2C8. The proposed mechanism, based on in vitro data, is decreased clearance due to vemurafenib-mediated inhibition of CYP450 2C8.', 'DDInter', 'Caution is advised if vemurafenib is used concomitantly with drugs that are substrates of CYP450 2C8, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever vemurafenib is added to or withdrawn from therapy with these drugs. Since vemurafenib has a long half-life, a wash-out period of 8 days following the discontinuation of vemurafenib has been suggested to avoid an interaction with subsequent therapy. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if vemurafenib is used concomitantly with drugs that are substrates of CYP450 2C8, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112058/', '', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, More', 1767369485);
