INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (112173, 'Temsirolimus', 'Tamoxifen', 'Moderate', 'Coadministration of sirolimus or tacrolimus with other drugs that are also metabolized by CYP450 3A4 may result in elevated plasma concentrations of the macrolide immunosuppressant and/or the coadministered drug(s). The mechanism is decreased drug clearance due to competitive inhibition of CYP450 3A4 activity.', 'DDInter', 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112304/', '', 'Darolutamide, Anastrozole, Fulvestrant, Abarelix, Flutamide, Relugolix, Degarelix, Nilutamide, Abiraterone', 'Acalabrutinib, Tepotinib, Avapritinib, Selumetinib, Capmatinib, Ibrutinib, Tivozanib, Zanubrutinib, Axitinib, Regorafenib, Ruxolitinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (112174, 'Terbinafine', 'Tamoxifen', 'Major', 'Chronic coadministration of potent or moderate CYP450 2D6 inhibitors including certain antidepressants may reduce the effectiveness of tamoxifen. The proposed mechanism is inhibition of tamoxifen bioactivation via CYP450 2D6 to endoxifen (4-hydroxy-N-desmethyltamoxifen), the active metabolite that may be responsible for much of tamoxifen''s antiestrogenic activity.', 'DDInter', 'Based on available data, patients treated with tamoxifen should avoid the chronic use of potent CYP450 2D6 inhibitors such as fluoxetine, paroxetine, and quinidine whenever possible, and preferably also moderate inhibitors such as bupropion, duloxetine, and sertraline. If an antidepressant is required during treatment with tamoxifen, agents such as desvenlafaxine, fluvoxamine, milnacipran, levomilnacipran, mirtazapine, and venlafaxine may be considered, since they have mild to no effects on CYP450 2D6. Alternatively, aromatase inhibitors such as anastrozole, exemestane, and letrozole may be appropriate substitutes for tamoxifen in certain patients. Alternatively, aromatase inhibitors such as anastrozole, exemestane, and letrozole may be appropriate substitutes for tamoxifen in certain patients.', 'Metabolism', 'Based on available data, patients treated with tamoxifen should avoid the chronic use of potent CYP450 2D6 inhibitors such as fluoxetine, paroxetine, and quinidine whenever possible, and preferably also moderate inhibitors such as bupropion, duloxetine, and sertraline.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112305/', '', 'Darolutamide, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Bicalutamide, Degarelix, Nilutamide, More', 'Salicylic acid, Selenium Sulfide, Thiabendazole, Miconazole, Ciclopirox, Econazole, Flucytosine, Tioconazole, Chlorphenesin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (112175, 'Terbutaline', 'Tamoxifen', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112306/', '', 'Darolutamide, Anastrozole, Fulvestrant, Letrozole, Aminoglutethimide, Exemestane', 'Ephedrine, Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Mometasone, Cromoglicic acid, Ipratropium, Epinephrine, Beclomethasone dipropionate', 1767369485);
