INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (112552, 'Vardenafil', 'Rifapentine', 'Moderate', 'Coadministration with rifapentine may decrease the plasma concentrations of drugs that are substrates of the CYP450 2C8, 2C9, and/or 3A4 isoenzymes. The mechanism is accelerated clearance due to induction of these isoenzymes by rifapentine. Enzyme activities may be induced within 4 days of the first dose and return to normal 14 days after discontinuation of rifapentine.', 'DDInter', 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever rifapentine is added to or withdrawn from therapy.', 'Metabolism', 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112683/', '', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Rifampicin, Cycloserine, Rifabutin, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (112553, 'Vardenafil', 'Riociguat', 'Major', 'Coadministration of riociguat with phosphodiesterase (PDE) inhibitors may cause significant hypotension. The mechanism involves peripheral vasodilation secondary to enhanced levels of cyclic guanosine monophosphate (cGMP) in vascular smooth muscle cells, as PDE inhibitors prevent degradation of cGMP while riociguat promotes its synthesis by stimulating soluble guanylate cyclase (sGC), an enzyme in the cardiopulmonary system that binds with nitric oxide (NO) to catalyze the synthesis of cGMP. Riociguat does not affect the pharmacokinetics of sildenafil.', 'DDInter', 'Concomitant use of riociguat with PDE inhibitors, including specific PDE-5 inhibitors (e.g., avanafil, sildenafil, tadalafil, vardenafil) or nonspecific PDE inhibitors (e.g., dipyridamole, theophylline), is considered contraindicated. Riociguat should be discontinued at least 24 hours before administering a PDE-5 inhibitor, and avoidance of riociguat administration within 24 hours after sildenafil or within 48 hours after tadalafil is recommended. Limited data exists for other PDE inhibitors.', 'Others', 'Concomitant use of riociguat with PDE inhibitors, including specific PDE-5 inhibitors (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112684/', '', 'Apomorphine, Fesoterodine, Pentosan polysulfate, Calcium chloride, Tolterodine, Phenazopyridine, Trospium, Mirabegron, Acetohydroxamic acid, Yohimbine, Flavoxate, More', 'Bosentan, Macitentan, Metyrosine', 1767369485);
