INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (113101, 'Trimethoprim', 'Rosiglitazone', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 may increase the plasma concentrations of rosiglitazone, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Given the potential for dose-related adverse events with rosiglitazone, caution is advised during coadministration with CYP450 2C8 inhibitors, particularly in patients already receiving a higher dosage of rosiglitazone (e.g., 8 mg/day). Close monitoring for the development of hypoglycemia and other adverse effects is recommended, such as fluid retention; weight gain; new or worsening heart failure; pulmonary, peripheral, and macular edema; angina; bone fractures; anemia; and liver enzyme elevations. Patients should regularly monitor their blood sugar and learn how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitation. The dosage of rosiglitazone may require adjustment if an interaction is suspected. Likewise, patients should be observed for potential loss of glycemic control following discontinuation of the CYP450 2C8 inhibitor, and the rosiglitazone dosage adjusted as necessary.', 'Metabolism', 'Given the potential for dose-related adverse events with rosiglitazone, caution is advised during coadministration with CYP450 2C8 inhibitors, particularly in patients already receiving a higher dosage of rosiglitazone (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113232/', '', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Tolbutamide, Miglitol, More', 'Sulfadiazine, Sulfamethoxazole, Sulfamethizole, Sulfisoxazole, Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Cycloserine, Ethambutol, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (113102, 'Sulfamethoxazole', 'Rucaparib', 'Moderate', 'Coadministration with rucaparib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 2C9. The proposed mechanism is decreased clearance due to rucaparib-mediated inhibition of CYP450 2C9.', 'DDInter', 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 2C9, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever rucaparib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 2C9, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113233/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Trimethoprim, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, More', 'Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, Enasidenib, More', 1767369485);
