INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (113157, 'Aluminum hydroxide', 'Naproxen', 'Minor', 'Antacids and some oral aluminum, calcium, or magnesium containing preparations may decrease the gastrointestinal absorption of enteric-coated naproxen, which requires an acidic environment for dissolution. The proposed mechanism is an increase in gastric pH (i.e. decreased gastric acidity) produced by antacids. In patients treated with antacids, the possibility of a reduced or subtherapeutic response to enteric-coated naproxen should be considered. Concomitant use of these drugs is generally not recommended. Aluminum hydroxide and some magnesium-containing antacids have also been reported to reduce the absorption of regular naproxen. The interaction may be minimized by separating the times of administration by at least 2 hours.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113288/', '', 'Ritodrine, Bremelanotide, Flibanserin, Cabergoline, Black cohosh, Bromocriptine, Ibuprofen, Rabeprazole, Phenylbutazone, Tolazoline, Ketoprofen, More', '', 1767369485);
