INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (113516, 'Amikacin (liposome)', 'Ticarcillin', 'Moderate', 'INTERVAL: Ticarcillin may inactivate aminoglycosides in vivo and in vitro. The mechanism is complexation with the aminoglycoside. Typically, this effect is significant only in patients with renal failure or if the agents are mixed in the same IV container or line. Lower serum aminoglycoside levels may be seen, and dosage adjustments may be necessary.', 'DDInter', 'Ticarcillin and aminoglycosides should be administered separately during combined therapy.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113649/', '', 'Gatifloxacin, Rifamycin, Lomefloxacin, Ampicillin, Ofloxacin, Acyclovir, Polymyxin B, Ciprofloxacin, Famciclovir, Cefuroxime, Trifluridine, More', 'Sulbactam, Oxacillin, Dicloxacillin, Meticillin, Carbenicillin, Bacampicillin, Tazobactam, Cloxacillin, Nafcillin, Benzylpenicillin, Ampicillin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (113517, 'Anisindione', 'Ticarcillin', 'Moderate', 'Penicillins may occasionally potentiate the risk of bleeding in patients treated with oral anticoagulants. The exact mechanism of interaction is unknown but may involve penicillin inhibition of platelet aggregation.', 'DDInter', 'Caution is recommended if a penicillin is prescribed during oral anticoagulant therapy, especially in the elderly and patients with uremia or hepatic impairment. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of penicillin therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is recommended if a penicillin is prescribed during oral anticoagulant therapy, especially in the elderly and patients with uremia or hepatic impairment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113650/', '', 'Sulbactam, Meticillin, Bacampicillin, Mezlocillin, Cloxacillin, Tazobactam', '', 1767369485);
