INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (113549, 'Tigecycline', 'Tacrolimus', 'Moderate', 'Coadministration of tigecycline with calcineurin inhibitors (e.g., tacrolimus, cyclosporine) may lead to an increase in serum trough concentrations of the calcineurin inhibitors. The mechanism for this interaction is unknown.', 'DDInter', 'Close monitoring is recommended whenever tigecycline is used concomitantly with a calcineurin inhibitor. Clinical and laboratory monitoring should be considered whenever tigecycline is added to or withdrawn from therapy with a calcineurin inhibitor, and the dosage of the calcineurin inhibitor adjusted as necessary. Patients should be monitored for development of adverse effects associated with the calcineurin inhibitor.', 'Others', 'Close monitoring is recommended whenever tigecycline is used concomitantly with a calcineurin inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113682/', '', 'Oxytetracycline, Omadacycline, Eravacycline, Demeclocycline, Sarecycline', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Finasteride, Ivermectin, Caffeine, Diclofenac, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (113550, 'Typhoid vaccine (live)', 'Tigecycline', 'Major', 'INTERVAL: The use of live, attenuated oral typhoid vaccine with antibacterial sulfonamides or other antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Salmonella typhi (Ty21a), thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', 'DDInter', 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics. A longer interval should be considered following treatment with long-acting antibiotics (e.g., azithromycin). If malaria prophylaxis is needed, the same 3-day interval at the minimum should be observed between antimalarials and the vaccine. However, chloroquine, mefloquine, and pyrimethamine-sulfadoxine may be given concomitantly with the vaccine. Alternatively, parenteral typhoid vaccine (typhoid Vi polysaccharide vaccine) may be considered if coadministration with antibacterial agents is required.', 'Others', 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113683/', '', '', '', 1767369485);
