INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (114082, 'Triheptanoin', 'Orlistat', 'Major', 'Coadministration of triheptanoin, a medium-chain triglyceride, with a pancreatic lipase inhibitor, such as orlistat, may reduce the systemic exposure of heptanoate, the active metabolite of triheptanoin, and diminish the therapeutic effects of triheptanoin. The primary function of pancreatic lipase inhibitors is to decrease gastrointestinal absorption of fats. Lipase inhibitors bind to lipase enzymes in the intestine and prevent the hydrolysis of dietary triglycerides into monoglycerides and fatty acids which results in a reduction in the absorption of dietary fat.', 'DDInter', 'Coadministration of triheptanoin with a pancreatic lipase inhibitor, such as orlistat, should be avoided.', 'Absorption', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114218/', '', 'Dexfenfluramine, Lorcaserin, Naltrexone, Ephedrine, Fenfluramine, Phentermine, Bupropion, Sibutramine, Diethylpropion, Mazindol', 'Teduglutide, Lonafarnib, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 1767369485);
