INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (114544, 'Itraconazole', 'Vilanterol', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the systemic exposure to vilanterol following oral inhalation, as it is primarily metabolized by the isoenzyme. The interaction has been studied with fluticasone-vilanterol and ketoconazole.', 'DDInter', 'Due to the risk of cardiovascular adverse effects such as increases in pulse rate and blood pressure and ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression, caution is recommended when medications containing vilanterol are coadministered with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. An increased risk of systemic corticosteroid effects should also be considered when fluticasone-vilanterol is used.', 'Metabolism', 'Due to the risk of cardiovascular adverse effects such as increases in pulse rate and blood pressure and ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression, caution is recommended when medications containing vilanterol are coadministered with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114680/', '', 'Caspofungin, Micafungin, Amphotericin B, Miconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Mometasone furoate, Terbutaline, Umeclidinium, Aclidinium, Glycopyrronium, Epinephrine, Olodaterol, Cromoglicic acid, Ipratropium, Orciprenaline, Pirbuterol, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (114545, 'Ivabradine', 'Vilanterol', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114681/', '', 'Mometasone furoate, Umeclidinium, Budesonide, Glycopyrronium, Aclidinium, Mometasone, Epinephrine, Fluticasone, Cromoglicic acid, Ipratropium, Tiotropium, Beclomethasone dipropionate', 'Alprostadil, Indomethacin, Ibuprofen, Ubidecarenone, Adenosine, Regadenoson, Felodipine, Acetylsalicylic acid, Amlodipine, Nifedipine', 1767369485);
