INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (114795, 'Vancomycin', 'Rofecoxib', 'Moderate', 'When administered concomitantly, vancomycin and ketorolac may have additive nephrotoxic effects. Ketorolac, like other nonsteroidal anti-inflammatory drugs (NSAIDs), inhibits vasodilatory renal prostaglandin synthesis. In addition, chronic use of NSAIDs may also be associated with renal toxicities, including renal failure. The mechanism of vancomycin-induced nephrotoxicity is unknown; however, the risk may be increased in patients with preexisting risk factors for nephrotoxicity, high vancomycin blood levels, or prolonged treatment.', 'DDInter', 'Caution is recommended in patients receiving ketorolac or other NSAIDs in combination with IV vancomycin, particularly in patients with preexisting risk factors for nephrotoxicity. Renal function and vancomycin therapeutic drug monitoring is recommended according to local policies and protocols. The dose and duration of concomitant NSAID therapy should also be minimized where possible.', 'Synergism', 'Caution is recommended in patients receiving ketorolac or other NSAIDs in combination with IV vancomycin, particularly in patients with preexisting risk factors for nephrotoxicity.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114932/', '', 'Tinidazole, Bacitracin, Polymyxin B, Oritavancin, Telavancin, Methenamine, Spectinomycin, Lefamulin, Fosfomycin, Tedizolid, Dalbavancin, More', 'Chondroitin sulfate, Esomeprazole, Glucosamine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (114796, 'Vancomycin', 'Sirolimus', 'Major', 'Coadministration of macrolide immunosuppressants with other nephrotoxic agents may increase the risk and/or severity of renal impairment due to additive adverse effects on the kidney. Clinical experience in coadministration with cyclosporine indicates increased renal toxicity as evidenced by increased serum creatinine and decreased glomerular filtration rate. An interaction with ibuprofen resulting in acute renal failure was suspected in two liver transplant patients who had been stabilized on tacrolimus.', 'DDInter', 'Caution is advised when macrolide immunosuppressants is used in patients who have recently received or are receiving treatment with other potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; cyclosporine; deferasirox; gallium nitrate; lithium; mesalamine; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be closely monitored both during and after discontinuation of therapy. Patients should be advised to seek medical attention if they experience symptoms that may indicate nephrotoxicity such as decreased urine output, sudden weight gain, fluid retention, edema, or shortness of breath.', 'Synergism', 'Caution is advised when macrolide immunosuppressants is used in patients who have recently received or are receiving treatment with other potentially nephrotoxic drugs (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114933/', '', 'Tinidazole, Oritavancin, Daptomycin, Methenamine, Spectinomycin, Fosfomycin, Tedizolid, Dalbavancin, Rifamycin, Paromomycin, Rifaximin, More', 'Alteplase, Ascorbic acid, Vitamin A, Potassium Iodide, Nandrolone, Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, More', 1767369485);
