INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (114804, 'Vancomycin', 'Tolmetin', 'Moderate', 'When administered concomitantly, vancomycin and ketorolac may have additive nephrotoxic effects. Ketorolac, like other nonsteroidal anti-inflammatory drugs (NSAIDs), inhibits vasodilatory renal prostaglandin synthesis. In addition, chronic use of NSAIDs may also be associated with renal toxicities, including renal failure. The mechanism of vancomycin-induced nephrotoxicity is unknown; however, the risk may be increased in patients with preexisting risk factors for nephrotoxicity, high vancomycin blood levels, or prolonged treatment.', 'DDInter', 'Caution is recommended in patients receiving ketorolac or other NSAIDs in combination with IV vancomycin, particularly in patients with preexisting risk factors for nephrotoxicity. Renal function and vancomycin therapeutic drug monitoring is recommended according to local policies and protocols. The dose and duration of concomitant NSAID therapy should also be minimized where possible.', 'Synergism', 'Caution is recommended in patients receiving ketorolac or other NSAIDs in combination with IV vancomycin, particularly in patients with preexisting risk factors for nephrotoxicity.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/114941/', '', 'Chondroitin sulfate, Esomeprazole, Glucosamine, Tolazoline, Dimethyl sulfoxide', 'Tinidazole, Linezolid, Bacitracin, Oritavancin, Polymyxin B, Telavancin, Metronidazole, Methenamine, Spectinomycin, Lefamulin, Nitrofurantoin, More', 1767369485);
