INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (114896, 'Demeclocycline', 'Vitamin A', 'Major', 'Concomitant use of vitamin A or related compounds (i.e., retinoids) with tetracyclines may increase the risk of pseudotumor cerebri, also known as benign intracranial hypertension. These agents individually have been associated with the development of pseudotumor cerebri and may have additive effects during coadministration. Both acute and chronic vitamin A intoxication have been identified as secondary causes of pseudotumor cerebri.', 'DDInter', 'Caution is advised if vitamin A is coadministered with tetracyclines. Concomitant use of most retinoids with tetracyclines is considered contraindicated. Patients treated with any of these agents should be advised to discontinue treatment and notify their physician immediately if they develop early symptoms of pseudotumor cerebri such as headache, nausea, vomiting, visual disturbances, vision loss, and papilledema. Although intracranial hypertension typically resolves after discontinuation of treatment, the possibility for permanent visual loss exists. Therefore, prompt ophthalmologic evaluation should be performed if visual disturbances occur. Since intracranial pressure may remain elevated for weeks after drug cessation, patients should be monitored until they stabilize.', 'Synergism', 'Caution is advised if vitamin A is coadministered with tetracyclines.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/115033/', '', 'Alteplase, Ascorbic acid, Sirolimus, Potassium Iodide, Cyclosporine, Acetylcysteine, Nandrolone, Heparin, Methacholine, Hexaminolevulinate, Secretin human, More', 'Tigecycline, Mupirocin, Rifamycin, Bacitracin, Chloramphenicol, Rifaximin, Gentamicin, Amikacin, Neomycin', 1767369485);
