INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (114901, 'Vitamin A', 'Selpercatinib', 'Moderate', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 2C8 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 2C8 by selpercatinib.', 'DDInter', 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 2C8, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever selpercatinib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 2C8, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/115038/', '', 'Alteplase, Ascorbic acid, Potassium Iodide, Acetylcysteine, Nandrolone, Heparin, Sorbitol, Caffeine, Edrophonium, Indocyanine green, Tolbutamide, More', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (114902, 'Vitamin A', 'Tretinoin', 'Major', 'Concomitant use of a retinoid with vitamin A or other retinoids may result in hypervitaminosis A and additive toxicities. Patients may be at increased risk for potentially serious adverse effects such as vision impairment, pseudotumor cerebri (benign intracranial hypertension), mucositis, esophagitis, hepatitis, hypertriglyceridemia, pancreatitis, ileitis, and colitis.', 'DDInter', 'Patients receiving retinoid therapy should avoid concurrent use of other retinoids or vitamin A supplements in excess of the recommended daily allowance. Patients should be made aware of vitamin A sources in vitamin supplements, fortified foods and over-the-counter medications, and be advised to contact their physician if they experience signs of hypervitaminosis A such as inflammation or bleeding of the gums or lips; dry, scaly skin; alopecia; pruritus; erythema; headache; vertigo; and nausea. Patients should discontinue the medication and seek immediate medical attention if they develop signs and symptoms of pseudotumour cerebri or hepatotoxicity.', 'Synergism', 'Patients receiving retinoid therapy should avoid concurrent use of other retinoids or vitamin A supplements in excess of the recommended daily allowance.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/115039/', '', 'Alteplase, Ascorbic acid, Sirolimus, Potassium Iodide, Acetylcysteine, Nandrolone, Sorbitol, Caffeine, Edrophonium, Indocyanine green, Tolbutamide, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (114903, 'Vorinostat', 'Acetohexamide', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/115040/', '', 'Simvastatin, Guar gum, Rosuvastatin', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Tisagenlecleucel, Tazemetostat, Altretamine, Ixazomib, Sotorasib, Enasidenib, Belzutifan, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (114904, 'Dicoumarol', 'Vorinostat', 'Moderate', 'Prolongation of prothrombin time and INR have been observed during clinical trials in patients receiving histone deacetylase inhibitors concomitantly with coumarin-derivative anticoagulants. The mechanism has not been described.', 'DDInter', 'Patients treated with a coumarin-derivative anticoagulant should be closely monitored during concomitant therapy with histone deacetylase inhibitors such as romidepsin and vorinostat. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of the histone deacetylase inhibitor in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Patients treated with a coumarin-derivative anticoagulant should be closely monitored during concomitant therapy with histone deacetylase inhibitors such as romidepsin and vorinostat.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/115041/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', 'Pertuzumab, Olaparib, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, More', 1767369485);
