INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (114941, 'Iron', 'Zinc acetate', 'Minor', 'Iron and zinc may reduce each other''s absorption when they are coadministered orally. The mechanism of interaction is unknown. In clinical studies, concurrent supplementation with iron and zinc was associated with reduced efficacy in improving the growth and iron and zinc status of malnourished infants compared to supplementation with either one alone. In any case, the clinical significance is probably minimal in healthy individuals. However, monitoring for reduced therapeutic response may be advisable in malnourished patients. Limited data suggest that the magnitude of the interaction may be lessened by separating the times of administration of each by 12 hours.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/115078/', '', 'Iron sucrose, Cyanocobalamin, Folic acid', 'Lidocaine, Diltiazem, Tetracaine, Fluocinolone acetonide, Hydrocortisone, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Prednisolone, Betamethasone, More', 1767369485);
