INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (116999, 'Insulin human (inhalation, rapid acting)', 'Iloperidone', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/117136/', '', 'Haloperidol, Pimavanserin, Thiothixene, Pimozide, Amisulpride, Loxapine, Molindone, Droperidol', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (117000, 'Insulin human (inhalation, rapid acting)', 'Iloprost', 'Moderate', 'INTERVAL: Bronchodilators and other orally inhaled products may alter the absorption of inhaled human insulin. In nondiabetic subjects with mild asthma (n=36) and moderate asthma (n=31), administration of albuterol 30 minutes prior to inhaled human insulin resulted in a mean increase in insulin systemic exposure (AUC).', 'DDInter', 'Consistent timing of dosing of other inhaled products relative to inhaled human insulin administration is recommended. Blood glucose should be monitored closely, and the insulin dose adjusted accordingly.', 'Absorption', 'Consistent timing of dosing of other inhaled products relative to inhaled human insulin administration is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/117137/', '', 'Insulin lispro, Insulin degludec, Lixisenatide, Insulin detemir, Insulin human, Insulin glargine, Liraglutide, Insulin glulisine', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, More', 1767369485);
