INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (118051, 'Ribociclib', 'Paliperidone', 'Major', 'Ribociclib can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of ribociclib with other drugs that can prolong the QT interval should generally be avoided. However, if concomitant use is required, caution and clinical and laboratory monitoring are recommended. ECGs should be assessed prior to initiation of treatment, during treatment when clinically necessary, and more frequently if QTcF prolongation occurs at any time during treatment. Ribociclib should be permanently discontinued if the QTcF interval prolongation is either greater than 500 msec or there is a greater than 60 msec change from baseline and associated with any of the following: Torsades de Pointes, polymorphic ventricular tachycardia, unexplained syncope, or signs/symptoms of serious arrhythmia. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of ribociclib with other drugs that can prolong the QT interval should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118189/', '', 'Loxapine, Molindone, Thiothixene', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (118052, 'Rifampicin', 'Paliperidone', 'Moderate', 'The concomitant use of potent inducers of P-glycoprotein (P-gp) efflux transporters such as carbamazepine may reduce plasma concentrations of paliperidone and its efficacy. The proposed mechanism is induction of renal P-gp. P-gp induction may take 2 to 3 weeks to reach steady state after a P-gp-inducing drug is initiated, and as long to wear off after the inducer is discontinued. Other P-gp inducers such as rifampin and St. John''s wort may also interact, although clinical data have not been reported.', 'DDInter', 'Patients should be monitored for signs of reduced efficacy during concomitant use and for 2 to 3 weeks after a P-gp-inducing drug is added, discontinued, or its dose is changed. Dose adjustments of paliperidone may be required.', 'Absorption', 'Patients should be monitored for signs of reduced efficacy during concomitant use and for 2 to 3 weeks after a P-gp-inducing drug is added, discontinued, or its dose is changed.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118190/', '', 'Pyrazinamide, Rifamycin, Streptomycin, Trimethoprim, Cycloserine, Rifabutin, Isoniazid, Ethambutol, Ethionamide, Pyridoxine, Pretomanid, Aminosalicylic acid', 'Chlorpromazine, Mesoridazine, Methotrimeprazine, Promazine, Fluphenazine, Asenapine, Perphenazine, Thiothixene, Thioridazine, Pimozide, Trifluoperazine, More', 1767369485);
