INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (118144, 'Boceprevir', 'Saquinavir', 'Moderate', 'Coadministration of boceprevir and some ritonavir-boosted protease inhibitor regimens has been associated with decreased plasma concentrations of both boceprevir and the protease inhibitors. The mechanism of interaction has not been described. Data are currently unavailable regarding a potential interaction between boceprevir and other protease inhibitors, whether alone or ritonavir-boosted.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, caution is advised if boceprevir is used in combination with ritonavir-boosted protease inhibitor regimens. Patients coinfected with chronic HCV and HIV who have been started on one of these combinations should be closely monitored for treatment response and potential HCV and HIV virologic rebound. The safety and efficacy of boceprevir has not been established in the HCV/HIV coinfected population.', 'Others', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, caution is advised if boceprevir is used in combination with ritonavir-boosted protease inhibitor regimens.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118282/', '', 'Lamivudine, Glecaprevir, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, More', 'Glecaprevir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Remdesivir, Famciclovir, Maribavir, Pibrentasvir, More', 1767369485);
