INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (118197, 'Fingolimod', 'Saquinavir', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of fingolimod treatment in patients receiving drugs that prolong the QT interval. Fingolimod can cause a decrease in heart rate during initiation of therapy that is apparent within an hour of the first dose and maximal at approximately 6 hours postdose in most cases, but occasionally up to 20 hours after the first dose.', 'DDInter', 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, close monitoring is recommended during initiation of fingolimod treatment in patients receiving concomitant drugs that can prolong the QT interval, patients with significant QT prolongation (QTc >470 msec in females or >450 msec in males), or patients with relevant risk factors for QT prolongation (e.g., hypokalemia, hypomagnesemia, congenital QT prolongation).', 'Synergism', 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118335/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (118198, 'Flecainide', 'Saquinavir', 'Major', 'Coadministration with saquinavir may significantly increase the plasma concentrations of some antiarrhythmic agents such as amiodarone, bepridil, flecainide, propafenone, and quinidine. The mechanism is decreased clearance due to inhibition of CYP450 3A4- and 2D6-mediated metabolism by saquinavir, particularly when coadministered with ritonavir as a pharmacokinetic booster. Although clinical data are lacking, the interaction could conceivably lead to serious and/or life-threatening reactions including QT interval prolongation and ventricular arrhythmias such as ventricular tachycardia and torsade de pointes.', 'DDInter', 'The use of saquinavir in combination with amiodarone, bepridil, flecainide, propafenone, and quinidine is considered contraindicated.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118336/', '', 'Tocainide, Bretylium, Mexiletine', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, Dolutegravir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (118199, 'Flibanserin', 'Saquinavir', 'Major', 'Coadministration with moderate and potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of flibanserin, which is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19. High plasma levels of flibanserin may increase the risk of severe hypotension, syncope, and central nervous system depression.', 'DDInter', 'Concomitant use of flibanserin with moderate or potent CYP450 3A4 inhibitors is considered contraindicated. When initiating flibanserin following treatment with a moderate or potent CYP450 3A4 inhibitor, the manufacturer recommends waiting until 2 weeks after the last dose of CYP450 3A4 inhibitor. Conversely, if a moderate or potent CYP450 3A4 inhibitor is required during flibanserin therapy, flibanserin should be discontinued at least 2 days before starting the CYP450 3A4 inhibitor.', 'Metabolism', 'Concomitant use of flibanserin with moderate or potent CYP450 3A4 inhibitors is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118337/', '', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, More', 'Bremelanotide, Black cohosh, Cabergoline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (118200, 'Fluconazole', 'Saquinavir', 'Major', 'Saquinavir in combination with ritonavir may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential related to their effects on cardiac conduction.', 'DDInter', 'Ritonavir-boosted saquinavir should not be used with other drugs that may prolong the QT interval. Patients treated with any medication that can cause QT prolongation should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Synergism, Absorption', 'Ritonavir-boosted saquinavir should not be used with other drugs that may prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118338/', '', 'Tinidazole, Tetracycline, Cefepime, Cefuroxime, Secnidazole, Cefixime, Amikacin, Flucytosine, Anidulafungin, Micafungin, Oteseconazole, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Brincidofovir, Elbasvir, Darunavir, More', 1767369485);
