INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (118321, 'Saquinavir', 'Mitotane', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118459/', '', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Porfimer sodium, Selinexor, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Valaciclovir, Telaprevir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 1767369485);
