INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (118386, 'Saquinavir', 'Pralsetinib', 'Major', 'Coadministration with a combined P-glycoprotein (P-gp) and potent CYP450 3A inhibitor may significantly increase the plasma concentrations of pralsetinib, which is primarily metabolized by the CYP450 3A isoenzyme. In vitro studies have shown pralsetinib is a time-dependent inhibitor of CYP450 3A4/5 isoenzymes and a P-gp inhibitor at clinically relevant concentrations.', 'DDInter', 'Concomitant use of pralsetinib with a combined P-gp and potent CYP450 3A inhibitor should be avoided when possible. Following discontinuation of the combined P-gp and potent CYP450 3A inhibitor, and after an appropriate washout period (3 to 5 elimination half-lives), the pralsetinib dose taken prior to initiating the combined P-gp and potent CYP450 3A4 inhibitor may be resumed.', 'Metabolism', 'Concomitant use of pralsetinib with a combined P-gp and potent CYP450 3A inhibitor should be avoided when possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118524/', '', 'Lamivudine, Cabotegravir, Zanamivir, Valaciclovir, Dolutegravir, Oseltamivir, Acyclovir, Stavudine, Remdesivir, Elbasvir, Famciclovir, More', 'Tivozanib, Trametinib, Dacomitinib', 1767369485);
