INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (118598, 'Iobenguane (I-131)', 'Palifermin', 'Moderate', 'INTERVAL: In a clinical trial, administration of palifermin within 24 hours of chemotherapy resulted in increased severity and duration of oral mucositis, presumably due to increased sensitivity of the rapidly dividing epithelial cells in the immediate post-chemotherapy period.', 'DDInter', 'Palifermin should not be administered within 24 hours before, during infusion of, or within 24 hours after administration of myelotoxic chemotherapy.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118736/', '', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (118599, 'Iobenguane (I-131)', 'Pamidronic acid', 'Moderate', 'Coadministration of bisphosphonates with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of bisphosphonates has been associated with nephrotoxicity manifested as deterioration of renal function and renal failure.', 'DDInter', 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides, polypeptide and polymyxin antibiotics, vancomycin, amphotericin B, adefovir, cidofovir, tenofovir, foscarnet, cisplatin, gallium nitrate, lithium, mesalamine, certain immunosuppressants, intravenous pentamidine, high intravenous dosages of methotrexate, high dosages of nonsteroidal anti-inflammatory agents). Renal function and serum electrolytes should be monitored. Patients should have serum creatinine assessed prior to each treatment, and treatment should be withheld for renal deterioration.', 'Synergism', 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118737/', '', 'Tiludronic acid, Alendronic acid, Vosoritide, Cholecalciferol, Burosumab, Risedronic acid, Romosozumab', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (118600, 'Iobenguane (I-131)', 'Pegfilgrastim', 'Moderate', 'INTERVAL: The safety and efficacy of hematopoietic growth factors such as colony-stimulating factors (G-CSF and GM-CSF) and stem cell factors (SCF) given simultaneously with cancer chemotherapy have not been established. Theoretical concerns exist regarding their concomitant administration because hematopoietic growth factors stimulate myeloid cell proliferation while antineoplastic agents primarily target rapidly dividing cells.', 'DDInter', 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, the manufacturer recommends that pegfilgrastim not be given from 14 days before until 24 hours after administration of antineoplastic agents.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118738/', '', 'Interferon alfa-n1, Oprelvekin, Elapegademase, Interferon beta-1b, Peginterferon alfa-2a, Peginterferon beta-1a, Interferon alfacon-1, Interferon beta-1a, Histamine, Aldesleukin, Pegademase, More', '', 1767369485);
