INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (118800, 'Iopromide', 'Valaciclovir', 'Major', 'Concomitant use of intravascular radiocontrast media with other nephrotoxic agents may potentiate the risk of contrast-induced nephropathy and renal impairment. While the condition is usually transient and asymptomatic, it can be associated with increased risk of renal failure, dialysis, prolonged hospitalization, significant long-term morbidity, and mortality.', 'DDInter', 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy. Any repeat procedures with contrast media, if necessary, should not occur until at least 48 to 72 hours after the previous contrast exposure and renal function has fully recovered.', 'Synergism', 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/118938/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, More', 'Ethiodized oil, Iopanoic acid, Tyropanoic acid', 1767369485);
