INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (119142, 'Irinotecan', 'Rufinamide', 'Moderate', 'Coadministration with inducers of the CYP450 3A4 isoenzyme may decrease the plasma concentrations of irinotecan and its pharmacologically active metabolite, SN-38. Irinotecan is partially metabolized by CYP450 3A4, and induction of this process results in less of the drug available in the plasma for conversion to SN-38 via carboxylesterases..', 'DDInter', 'The antitumour activity of irinotecan may be reduced in patients treated with CYP450 3A4 inducers. Pharmacologic response to irinotecan should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the irinotecan dosage adjusted as necessary.', 'Metabolism', 'The antitumour activity of irinotecan may be reduced in patients treated with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119280/', '', 'Cabazitaxel, Paclitaxel, Topotecan, Teniposide, Vinorelbine', 'Brivaracetam, Ezogabine, Lamotrigine, Ethosuximide, Lacosamide, Tiagabine, Ethotoin, Paramethadione, Perampanel, Ganaxolone, Vigabatrin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (119143, 'Irinotecan', 'Sacituzumab govitecan', 'Major', 'Coadministration of sacituzumab govitecan with irinotecan may increase the risk of adverse effects, such as severe neutropenia, nausea, vomiting, and diarrhea. The proposed mechanism involves increased exposure to SN-38, a potent topoisomerase I inhibitor. SN-38 is the pharmacologically active metabolite of irinotecan, and considered to be approximately 1000 times more potent than irinotecan. However, SN-38 is also delivered to Trop-2-expressing cancer cells via the Trop-2-directed antibody drug conjugate sacituzumab govitecan-hziy. SN-38 is covalently attached to sacituzumab govitecan, which binds to Trop-2-expressing cancer cells and allows for internalization of SN-38 into these cells to interact with topoisomerase I.', 'DDInter', 'Sacituzumab govitecan should not be substituted for or used with other drugs containing irinotecan or the active metabolite SN-38.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119281/', '', 'Trabectedin, Cabazitaxel, Vinblastine, Paclitaxel, Vincristine, Teniposide, Vinorelbine, Etoposide, Docetaxel', 'Pertuzumab, Cetuximab, Avelumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, Pembrolizumab, More', 1767369485);
