INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (119182, 'Irinotecan', 'Troglitazone', 'Moderate', 'Coadministration with inducers of the CYP450 3A4 isoenzyme may decrease the plasma concentrations of irinotecan and its pharmacologically active metabolite, SN-38. Irinotecan is partially metabolized by CYP450 3A4, and induction of this process results in less of the drug available in the plasma for conversion to SN-38 via carboxylesterases..', 'DDInter', 'The antitumour activity of irinotecan may be reduced in patients treated with CYP450 3A4 inducers. Pharmacologic response to irinotecan should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the irinotecan dosage adjusted as necessary.', 'Metabolism', 'The antitumour activity of irinotecan may be reduced in patients treated with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119320/', '', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Tolbutamide, Miglitol, More', 'Topotecan, Teniposide, Etoposide, Vinorelbine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (119183, 'Irinotecan', 'Troleandomycin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of irinotecan and its active metabolite, SN-38. CYP450 3A4 is the isoenzyme responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC. Inhibition of APC formation results in more irinotecan metabolism to SN-38, an active and toxic metabolite. High plasma levels of irinotecan and SN-38 may increase the risk of potentially fatal toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', 'DDInter', 'Concomitant use of irinotecan with potent CYP450 3A4 inhibitors should generally be avoided. Potent CYP450 3A4 inhibitors should be discontinued for at least one week before initiation of treatment with irinotecan.', 'Metabolism', 'Concomitant use of irinotecan with potent CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119321/', '', 'Topotecan, Teniposide, Etoposide', 'Lincomycin, Azithromycin, Dirithromycin', 1767369485);
